// Get input data from nodes
const customValuesData = $('Get Custom Values GHL')?.item?.json?.custom_values || "{}";
const customFieldsData = $('Get Custom Fields GHL')?.item?.json?.custom_fields || "{}";
const contactData = $('Search Contacts GHL')?.item?.json?.contact_details || "{}";
const formattedNotes = $('Code Parse Notes')?.item?.json?.readableNotes || "";
const formattedConversations = $('Code Parse Notes')?.item?.json?.readableConversations || "";
const calendarData = $('Get Calendars GHL')?.item?.json?.calendars || "{}";
const accountFields = $('Set Account Fields')?.item?.json || {};

// Get webhook data - this contains all field values
const webhookData = $('Webhook')?.item?.json?.body || {};

// Safely parse JSON strings to objects
function safeJsonParse(jsonStr, defaultValue = {}) {
  try {
    return typeof jsonStr === 'string' ? JSON.parse(jsonStr) : jsonStr;
  } catch (e) {
    console.log(`Error parsing JSON: ${e.message}`);
    return defaultValue;
  }
}

// Parse the JSON strings to objects
const customValuesObj = safeJsonParse(customValuesData);
const customFieldsObj = safeJsonParse(customFieldsData);
const contactObj = safeJsonParse(contactData);
const calendarObj = safeJsonParse(calendarData);

// Extract the contact data from the first contact in the array
const contact = contactObj?.contacts && Array.isArray(contactObj.contacts) && contactObj.contacts.length > 0 
  ? contactObj.contacts[0] 
  : {};

// Extract custom fields array from both sources
const accountCustomFields = Array.isArray(customFieldsObj?.customFields) ? customFieldsObj.customFields : [];
const contactCustomFields = Array.isArray(contact?.customFields) ? contact.customFields : [];
const accountCustomValues = Array.isArray(customValuesObj?.customValues) ? customValuesObj.customValues : [];
const calendars = Array.isArray(calendarObj?.calendars) ? calendarObj.calendars : [];

// Format contact name with proper capitalization
const firstName = contact?.firstNameLowerCase ? 
  contact.firstNameLowerCase.charAt(0).toUpperCase() + contact.firstNameLowerCase.slice(1) : 
  '';
const lastName = contact?.lastNameLowerCase ? 
  contact.lastNameLowerCase.charAt(0).toUpperCase() + contact.lastNameLowerCase.slice(1) : 
  '';
const fullName = `${firstName} ${lastName}`.trim() || 'none';

// Get essential values from configuration
const voiceAgentName = "Finn";
const voiceAgentId = "agent_9cd79204b446b2dd69e63de808";

// Create calendar mapping
const calendarMap = {};
calendars.forEach(calendar => {
  if (!calendar) return;

  const calendarName = calendar.name || "";
  const snakeCaseName = calendarName
    .toLowerCase()
    .replace(/[^\w\s]/g, '')
    .replace(/\s+/g, '_');

  calendarMap[calendarName] = calendar.id || '';
  calendarMap[snakeCaseName] = calendar.id || '';
});

// Get connection details from Set Account Fields
const toNumber = String(accountFields?.to_number || webhookData?.phone || '');
const fromNumber = String(accountFields?.from_number || webhookData?.['Assigned Phone Number'] || '');
const privateIntegrationToken = String(accountFields?.Authorization || webhookData?.customData?.['Private Integration Token'] || '');
const locationId = String(accountFields?.locationId || (webhookData?.location?.id || ''));
const accountTimezone = String(accountFields?.['Account Timezone'] || 'America/New_York');

// Create custom field name to ID mapping
const fieldNameToId = {};
accountCustomFields.forEach(field => {
  if (!field?.name) return;

  const snakeCaseName = field.name
    .toLowerCase()
    .replace(/[^\w\s]/g, '')
    .replace(/\s+/g, '_');

  fieldNameToId[field.name] = field.id;
  fieldNameToId[snakeCaseName] = field.id;
});

// Start building dynamic variables with basic info
const dynamicVariables = {
  // Basic contact info
  name: String(firstName || webhookData?.first_name || 'none'),
  phone: String(contact?.phone || webhookData?.phone || 'none'),
  email: String(contact?.email || webhookData?.email || 'none'),
  address: String(contact?.address || webhookData?.full_address || 'none'),
  full_name: String(fullName || (webhookData?.full_name || 'none')),
  business_name: String(contact?.businessName || contact?.companyName || webhookData?.company_name || 'none'),
  contact_id: String(contact?.id || webhookData?.contact_id || ''),
  location_id: locationId,
  private_integration_token: privateIntegrationToken,
  account_company_name: "INFINITX AI",
  from_number: fromNumber,
  to_number: toNumber,
  account_timezone: accountTimezone,
  voice_ai_agent_name: voiceAgentName,
  voice_ai_agent_id: voiceAgentId,

  // Calendar IDs 
  sales_calendar_id: "cW52AjPgQBW9oj8xgKZz",
  sales2_calendar_id: "cW52AjPgQBW9oj8xgKZz",
  onboarding_calendar_id: "pk0fNghOxpLWocCxQgfW",
  customer_success_calendar_id: "fj2cWOY9KUqowNzokkeR",

  // Add conversation and notes history
  conversation_history: formattedConversations,
  notes_history: formattedNotes,

  // Tags
  tags: Array.isArray(contact?.tags) ? contact.tags.join(', ') : (webhookData?.tags || '')
};

// Directly add values from webhook data - this is the most important part!
// The values come directly from the webhook payload
for (const [key, value] of Object.entries(webhookData)) {
  // Skip some special fields and objects
  if (key === 'location' || key === 'user' || key === 'workflow' || 
      key === 'customData' || key === 'triggerData') {
    continue;
  }

  if (key && value !== undefined && value !== null) {
    // Convert to snake_case
    const snakeCaseName = key
      .toLowerCase()
      .replace(/[^\w\s]/g, '')
      .replace(/\s+/g, '_');

    if (snakeCaseName) {
      // Directly add the value
      dynamicVariables[snakeCaseName] = String(value || '');
    }
  }
}

// Add all custom field IDs with suffix _id
accountCustomFields.forEach(field => {
  if (!field?.id || !field?.name) return;

  const snakeCaseName = field.name
    .toLowerCase()
    .replace(/[^\w\s]/g, '')
    .replace(/\s+/g, '_');

  // Add the field ID with _id suffix
  dynamicVariables[`${snakeCaseName}_id`] = String(field.id);

  // Only add the field value if not already set from webhook
  // First look in webhookData by original field name
  if (dynamicVariables[snakeCaseName] === undefined) {
    if (webhookData[field.name] !== undefined) {
      dynamicVariables[snakeCaseName] = String(webhookData[field.name] || '');
    }
  }
});

// Add calendar_map
dynamicVariables.calendar_map = JSON.stringify({
  "Sales Calendar - AI Growth Partner": "cW52AjPgQBW9oj8xgKZz",
  "sales_calendar_ai_growth_partner": "cW52AjPgQBW9oj8xgKZz", 
  "Customer Success Calendar": "fj2cWOY9KUqowNzokkeR",
  "customer_success_calendar": "fj2cWOY9KUqowNzokkeR",
  "Onboarding Calendar": "pk0fNghOxpLWocCxQgfW",
  "onboarding_calendar": "pk0fNghOxpLWocCxQgfW"
});

// Format the final output
const callInbound = {
  call_inbound: {
    dynamic_variables: dynamicVariables
  }
};

// Convert the object to a properly formatted JSON string
const jsonString = JSON.stringify(callInbound, null, 2);

// Return an object with the jsonString as an easily mappable property
return { jsonBody: jsonString };

// Improved notes parser that's more dynamic and robust
function processData() {
  try {
    // Get the raw outputs from the nodes
    const conversationsRaw = $('Get Messages By Conversation ID GHL').item.json.conversations_output;
    const notesRaw = $('Get Notes GHL').item.json.notes_output;

    // Parse the JSON strings
    const conversationsData = JSON.parse(conversationsRaw);
    const notesData = JSON.parse(notesRaw);

    // Format the data for humans and AI consumption
    const readableConversations = formatConversations(conversationsData);
    const readableNotes = formatNotesImproved(notesData);

    // Return the result
    return [{ 
      json: {
        readableConversations: readableConversations,
        readableNotes: readableNotes
      } 
    }];
  } catch (error) {
    return [{ json: { error: error.message } }];
  }
}

// Format conversations (keeping existing implementation)
function formatConversations(data) {
  let result = "== CONVERSATION HISTORY ==\n\n";

  if (!data.messages || !data.messages.messages || data.messages.messages.length === 0) {
    return result + "No conversations found.";
  }

  const messages = data.messages.messages;

  for (let i = 0; i < messages.length; i++) {
    const msg = messages[i];

    // Add date
    result += "[" + new Date(msg.dateAdded).toLocaleString() + "] ";

    // Add direction
    if (msg.direction === "inbound") {
      result += "INCOMING ";
    } else {
      result += "OUTGOING ";
    }

    // Add type
    if (msg.messageType === "TYPE_ACTIVITY_OPPORTUNITY") {
      result += "Opportunity Activity";
    } else if (msg.messageType === "TYPE_SMS") {
      result += "SMS";
    } else if (msg.messageType === "TYPE_CALL") {
      result += "Call";
    } else {
      result += msg.messageType;
    }

    // Add status if available
    if (msg.status) {
      result += " (" + msg.status + ")";
    }

    result += ":\n";

    // Add body if available
    if (msg.body) {
      result += msg.body + "\n";
    }

    // Add separator
    if (i < messages.length - 1) {
      result += "\n--------------------------------------------------\n\n";
    }
  }

  return result;
}

// Improved notes formatter that's more flexible
function formatNotesImproved(data) {
  let result = "== CONTACT NOTES ==\n\n";

  if (!data.notes || data.notes.length === 0) {
    return result + "No notes found.";
  }

  const notes = data.notes;

  for (let i = 0; i < notes.length; i++) {
    const note = notes[i];

    result += "NOTE CREATED: " + new Date(note.dateAdded).toLocaleString() + "\n\n";

    // Extract data dynamically from the note body
    try {
      // Get the note body and try to parse key-value pairs
      const noteBody = note.body;

      // Check if the note body looks like it contains JSON-like key-value pairs
      if (noteBody.includes("\"key\"") && noteBody.includes("\"field_value\"")) {
        // Parse the pseudo-JSON structure
        const entries = extractKeyValuePairs(noteBody);

        // Group entries by category
        const callDetails = {};
        let transcript = "";
        let summary = "";

        // Process each entry
        entries.forEach(entry => {
          const key = entry.key;
          const value = entry.value;

          // Handle special cases
          if (key === "AI Call Transcript") {
            transcript = value;
          } else if (key === "AI Call Summary") {
            summary = value;
          } else {
            // Store other fields in the details object
            callDetails[key] = value;
          }
        });

        // Output formatted data (Details first)
        result += "=== CALL DETAILS ===\n";

        // Organize details into categories for better readability
        const categories = {
          "Basic Info": ["AI Call Type", "AI Call Contact Name", "AI Call Duration"],
          "Outcome": ["AI Call Outcome", "AI Call Outcome Reason"],
          "Appointment": ["AI Call Appointment DateTime"],
          "Contact Info": ["AI Call Contact Email", "AI Call Contact Phone"],
          "Technical": ["AI Call Recording", "AI Call Timestamp", "AI Call Ended Reason"]
        };

        // Display details by category
        for (const [category, fields] of Object.entries(categories)) {
          let categoryHasValues = false;
          let categoryContent = "";

          fields.forEach(field => {
            if (callDetails[field] && callDetails[field] !== "none" && callDetails[field] !== "not_detected") {
              categoryContent += `• ${field.replace('AI Call ', '')}: ${callDetails[field]}\n`;
              categoryHasValues = true;
            }
          });

          if (categoryHasValues) {
            result += `\n${category}:\n${categoryContent}`;
          }
        }

        // Add any remaining fields that weren't in specific categories
        let otherFields = "";
        for (const [key, value] of Object.entries(callDetails)) {
          if (value && value !== "none" && value !== "not_detected") {
            // Check if this field was already included in a category
            let alreadyIncluded = false;
            for (const fields of Object.values(categories)) {
              if (fields.includes(key)) {
                alreadyIncluded = true;
                break;
              }
            }

            if (!alreadyIncluded) {
              otherFields += `• ${key.replace('AI Call ', '')}: ${value}\n`;
            }
          }
        }

        if (otherFields) {
          result += "\nOther Details:\n" + otherFields;
        }

        // Add summary if present
        if (summary) {
          result += "\n=== CALL SUMMARY ===\n" + summary + "\n";
        }

        // Add transcript if present (full transcript, but with improved formatting)
        if (transcript) {
          result += "\n=== FULL TRANSCRIPT ===\n\n";

          // Improve transcript readability by formatting agent/user exchanges
          const formattedTranscript = formatTranscript(transcript);
          result += formattedTranscript + "\n";
        }
      } else {
        // If not in the expected format, just include the raw note body
        result += "NOTE CONTENT:\n" + noteBody + "\n";
      }
    } catch (error) {
      // Fallback if parsing fails
      result += "NOTE CONTENT (raw):\n" + note.body + "\n";
    }

    // Add separator between notes
    if (i < notes.length - 1) {
      result += "\n" + "=".repeat(50) + "\n\n";
    }
  }

  return result;
}

// Helper function to format transcript for better readability
function formatTranscript(transcript) {
  // Replace escaped newlines with actual newlines
  let formatted = transcript.replace(/\\n/g, '\n');

  // Split into lines
  const lines = formatted.split('\n');
  let result = '';

  // Process each line
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    if (line.startsWith('Agent:')) {
      // Format agent lines
      result += line + '\n';
    } else if (line.startsWith('User:')) {
      // Add spacing before user responses and format
      if (i > 0) result += '\n';
      result += line + '\n';
    } else {
      // Add other lines as-is
      result += line + '\n';
    }
  }

  return result;
}


// Helper function to extract key-value pairs from the pseudo-JSON structure
function extractKeyValuePairs(text) {
  const entries = [];
  const lines = text.split("\n");

  let currentKey = null;
  let currentValue = null;

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Look for key lines
    if (line.includes("\"key\"") && line.includes(":")) {
      // Extract the key name
      const keyMatch = line.match(/\"key\":\s*\"([^\"]+)\"/);
      if (keyMatch && keyMatch[1]) {
        currentKey = keyMatch[1];
      }
    }

    // Look for field_value lines
    if (line.includes("\"field_value\"") && line.includes(":")) {
      // Extract the value
      const valueMatch = line.match(/\"field_value\":\s*\"(.*?)\"/);
      if (valueMatch) {
        // Handle escaped quotes within the value
        currentValue = valueMatch[1].replace(/\\"/g, '"').replace(/\\\\n/g, '\n');

        // If we have both key and value, add them to our entries
        if (currentKey) {
          entries.push({
            key: currentKey,
            value: currentValue
          });

          // Reset for next pair
          currentKey = null;
          currentValue = null;
        }
      }
    }
  }

  return entries;
}

// Run the function and return the result
return processData();

// This code extracts specific fields from the previous node output
// for creating personalized messages

// Get the output from the previous node (Match Final Values)
const inputData = $input.item.json.jsonBody || "{}";

try {
  // Parse the JSON string from the previous node
  const parsedData = typeof inputData === 'string' ? JSON.parse(inputData) : inputData;

  // Access the dynamic_variables object
  const dynamicVars = parsedData?.call_inbound?.dynamic_variables || {};

  // Extract only the fields we need for personalization
  const personalizedData = {
    // Contact basics
    first_name: dynamicVars.first_name || dynamicVars.name || "",
    last_name: dynamicVars.last_name || "",
    full_name: dynamicVars.full_name || "",
    company_name: dynamicVars.company_name || dynamicVars.business_name || "",
    country: dynamicVars.country || "",
    date_created: dynamicVars.date_created || "",
    full_address: dynamicVars.full_address || dynamicVars.address || "",

    // Professional details
    title: dynamicVars.title || "",
    about: dynamicVars.about || "",
    phone_direct: dynamicVars.phone_direct || "",
    phone_hq: dynamicVars.phone_hq || "",

    // Company info
    industries: dynamicVars.industries || "",
    employees: dynamicVars.employees || "",
    revenue: dynamicVars.revenue || "",
    business_description: dynamicVars.business_description || "",
    employed_date: dynamicVars.employed_date || "",
    founded_year: dynamicVars.founded_year || "",
    sic_codes: dynamicVars.sic_codes || "",
    naics_codes: dynamicVars.naics_codes || "",
    product_services: dynamicVars.product_services || "",
    executives: dynamicVars.executives || "",
    similar_companies: dynamicVars.similar_companies || ""
  };

  // Create both text-friendly and JSON outputs for flexibility

  // Text summary for easy reading (e.g., for AI prompts)
  const textSummary = `
Contact: ${personalizedData.full_name} (${personalizedData.title})
Company: ${personalizedData.company_name}
Industry: ${personalizedData.industries}
Size: ${personalizedData.employees} employees, Revenue: ${personalizedData.revenue}
Location: ${personalizedData.full_address}
Direct Phone: ${personalizedData.phone_direct}
HQ Phone: ${personalizedData.phone_hq}

About Contact:
${personalizedData.about}

Business Description:
${personalizedData.business_description}

Products/Services: ${personalizedData.product_services}
Founded: ${personalizedData.founded_year}
Key Executives: ${personalizedData.executives}
Similar Companies: ${personalizedData.similar_companies}
`.trim();

  // Return both formats for maximum flexibility
  return {
    json: {
      personalization: personalizedData,  // JSON format for programmatic use
      summary: textSummary                // Text format for direct use in messages
    }
  };

} catch (error) {
  // Handle any errors that occur during processing
  return {
    json: {
      error: true,
      message: `Error processing data: ${error.message}`,
      inputData: inputData
    }
  };
}

